import React from "react";


class App extends React.Component{

    constructor() {
        super();

        this.state={
            string:"You are a talented programmer"
        };
    }

    render() {
        return ( <div>
            <h1> {this.state.string} </h1>
            <button onClick={this.setState({string:"I told you can do it"})} > Change the Text</button>
        </div> )
    }
}
export default App;
