import React from "react";

import {CardList} from "./components/card-component-folder/cardList";

class App extends React.Component {

    constructor() {
        super();

        this.state={
            heroes:[],
            searchField:''}

    }

    componentDidMount() {
        fetch("https://jsonplaceholder.typicode.com/users")
            .then(response => response.json())
            .then(users => this.setState({heroes:users}));
    }

    render() {
        return (
        <div>
            <input type="search" placeholder="Search monsetrs" onChange={e=>{
                this.setState({searchField:e.target.value},
                    ()=>(console.log(this.state)))}}/>
            <CardList heroes={this.state.heroes} />
        </div>
        )
    }
}
export default App;
